import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { toast } from 'react-hot-toast';
import { FiUser, FiMail, FiLock } from 'react-icons/fi';

const API_BASE = 'http://localhost:5001/api';

const Register = ({ setUser }) => {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    email: '',
    password: ''
  });

  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name || !form.email || !form.password) {
      toast.error('All fields are required');
      return;
    }

    try {
      setLoading(true);

      const res = await axios.post(`${API_BASE}/auth/register`, {
        name: form.name,
        email: form.email,
        password: form.password
      });

      const user = {
        id: res.data.user._id,
        name: res.data.user.name,
        email: res.data.user.email,
        walletBalance: res.data.user.wallet_balance
      };

      localStorage.setItem('flightUser', JSON.stringify(user));
      setUser(user);

      toast.success('Account created successfully üéâ');
      navigate('/');
    } catch (err) {
      toast.error(err.response?.data?.error || 'Registration failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-50 to-pink-50">
      <div className="card w-full max-w-md p-8 fade-in">
        <h2 className="text-3xl font-bold text-purple-900 text-center mb-6">
          Create Account
        </h2>

        <form onSubmit={handleSubmit} className="space-y-5">
          <div>
            <label className="label">
              <FiUser className="inline mr-2" /> Full Name
            </label>
            <input
              type="text"
              name="name"
              className="input-field"
              value={form.name}
              onChange={handleChange}
              placeholder="John Doe"
              required
            />
          </div>

          <div>
            <label className="label">
              <FiMail className="inline mr-2" /> Email
            </label>
            <input
              type="email"
              name="email"
              className="input-field"
              value={form.email}
              onChange={handleChange}
              placeholder="john@example.com"
              required
            />
          </div>

          <div>
            <label className="label">
              <FiLock className="inline mr-2" /> Password
            </label>
            <input
              type="password"
              name="password"
              className="input-field"
              value={form.password}
              onChange={handleChange}
              placeholder="‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢‚Ä¢"
              required
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            className="btn-primary w-full"
          >
            {loading ? 'Creating account...' : 'Register'}
          </button>
        </form>

        <p className="text-center text-sm text-purple-700 mt-6">
          Already have an account?{' '}
          <Link to="/login" className="font-semibold underline">
            Login
          </Link>
        </p>

        <div className="mt-4 text-center text-xs text-purple-500">
          üéÅ Welcome bonus: ‚Çπ50,000 wallet balance
        </div>
      </div>
    </div>
  );
};

export default Register;
