import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import { Toaster, toast } from 'react-hot-toast';
import Navbar from './components/Navbar';
import FlightSearch from './components/FlightSearch';
import FlightList from './components/FlightList';
import BookingForm from './components/BookingForm';
import Wallet from './components/Wallet';
import BookingHistory from './components/BookingHistory';
import './styles/purple-theme.css';

const App = () => {
  // const [user, setUser] = useState({
  //   id: 'user_001',
  //   name: 'John Doe',
  //   email: 'john@example.com',
  //   walletBalance: 50000
  // });
  const [user, setUser] = useState(null);
if (!user) {
  return <div className="text-center mt-20 text-purple-600">Loading user...</div>;
}

useEffect(() => {
  const initUser = async () => {
    const saved = localStorage.getItem('flightUser');
    if (saved) {
      setUser(JSON.parse(saved));
      return;
    }

    const res = await axios.post('http://localhost:5001/api/users/init', {
      name: 'John Doe',
      email: 'john@example.com'
    });

    const newUser = {
      id: res.data._id,
      name: res.data.name,
      email: res.data.email,
      walletBalance: res.data.wallet_balance
    };

    localStorage.setItem('flightUser', JSON.stringify(newUser));
    setUser(newUser);
  };

  initUser();
}, []);

  const [flights, setFlights] = useState([]);
  const [selectedFlight, setSelectedFlight] = useState(null);
  const [bookings, setBookings] = useState([]);

  // Mock user authentication
  useEffect(() => {
    // In a real app, this would be from localStorage or API
    const savedUser = localStorage.getItem('flightUser');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
  }, []);

  // Fetch bookings on load
  useEffect(() => {
    fetchBookings();
  }, [user.id]);

  const fetchBookings = async () => {
    try {
      const response = await axios.get(`http://localhost:5001/api/bookings/user/${user.id}`);
      setBookings(response.data);
    } catch (error) {
      console.error('Error fetching bookings:', error);
    }
  };

  const handleSearchFlights = async (searchParams) => {
    try {
      const response = await axios.get('http://localhost:5001/api/flights/search', {
        params: searchParams
      });
      setFlights(response.data);
      toast.success(`Found ${response.data.length} flights`);
    } catch (error) {
      toast.error('Error searching flights');
    }
  };

  const handleBookingAttempt = async (flightId) => {
    try {
      const response = await axios.post(`http://localhost:5001/api/flights/${flightId}/attempt`, {
        userId: user.id
      });

      // Update flight price locally
      setFlights(flights.map(flight => 
        flight.flight_id === flightId 
          ? { ...flight, current_price: response.data.current_price }
          : flight
      ));

      if (response.data.surge_active) {
        toast.error('⚠️ Price increased due to high demand!', {
          duration: 4000,
          icon: '⚠️'
        });
      }

      return response.data;
    } catch (error) {
      console.error('Booking attempt failed:', error);
      return null;
    }
  };

  const handleBookFlight = async (flightId, passengerInfo) => {
    try {
      const response = await axios.post('http://localhost:5001/api/bookings', {
        userId: user.id,
        flightId,
        passengerInfo
      });

      if (response.data.success) {
        // Update user wallet
        setUser(prev => ({
          ...prev,
          walletBalance: response.data.new_balance
        }));

        // Save to localStorage
        localStorage.setItem('flightUser', JSON.stringify({
          ...user,
          walletBalance: response.data.new_balance
        }));

        // Add to bookings
        setBookings(prev => [response.data.booking, ...prev]);

        toast.success('Booking successful! Downloading ticket...');

        // Download ticket
        setTimeout(() => {
          window.open(`http://localhost:5001/api/bookings/ticket/${response.data.booking.pnr}`, '_blank');
        }, 1000);

        return true;
      }
    } catch (error) {
      if (error.response?.data?.error === 'Insufficient wallet balance') {
        toast.error(`Insufficient balance. Required: ₹${error.response.data.required}, Available: ₹${error.response.data.available}`);
      } else {
        toast.error('Booking failed');
      }
      return false;
    }
  };

  const handleAddFunds = async (amount) => {
    try {
      const response = await axios.post(`http://localhost:5001/api/wallet/${user.id}/add`, {
        amount
      });

      if (response.data.success) {
        setUser(prev => ({
          ...prev,
          walletBalance: response.data.new_balance
        }));

        localStorage.setItem('flightUser', JSON.stringify({
          ...user,
          walletBalance: response.data.new_balance
        }));

        toast.success(`Added ₹${amount} to wallet`);
        return true;
      }
    } catch (error) {
      toast.error('Failed to add funds');
      return false;
    }
  };

  return (
    <Router>
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50">
        <Toaster 
          position="top-right"
          toastOptions={{
            style: {
              background: '#6B46C1',
              color: '#fff',
            },
          }}
        />

        <Navbar user={user} />

        <div className="container mx-auto px-4 py-8">
          <Routes>
            <Route path="/" element={
              <div className="fade-in">
                <div className="text-center mb-12">
                  <h1 className="text-4xl font-bold text-purple-900 mb-4">
                    ✈️ Find Your Perfect Flight
                  </h1>
                  <p className="text-purple-600 text-lg">
                    Book flights with dynamic pricing and exclusive deals
                  </p>
                </div>

                <FlightSearch onSearch={handleSearchFlights} />

                {flights.length > 0 && (
                  <FlightList 
                    flights={flights} 
                    onSelectFlight={setSelectedFlight}
                    onBookingAttempt={handleBookingAttempt}
                  />
                )}
              </div>
            } />

            <Route path="/book" element={
              selectedFlight ? (
                <BookingForm 
                  flight={selectedFlight}
                  user={user}
                  onBook={handleBookFlight}
                />
              ) : (
                <Navigate to="/" />
              )
            } />

            <Route path="/wallet" element={
              <Wallet 
                user={user}
                onAddFunds={handleAddFunds}
              />
            } />

            <Route path="/history" element={
              <BookingHistory 
                bookings={bookings}
                onDownloadTicket={(pnr) => {
                  window.open(`http://localhost:5001/api/bookings/ticket/${pnr}`, '_blank');
                }}
              />
            } />
          </Routes>
        </div>

        <footer className="bg-purple-900 text-white py-8 mt-12">
          <div className="container mx-auto px-4 text-center">
            <p className="text-purple-200">© 2024 Flight Booking System. All rights reserved.</p>
            <p className="text-purple-300 mt-2">Built with ❤️ using MERN Stack</p>
          </div>
        </footer>
      </div>
    </Router>
  );
};

export default App;
