                            <div className="flex-1 min-w-0">
                                <div className="flex items-center gap-4 mb-3">
                                    <span className="text-xl font-bold text-purple-900">{flight.airline}</span>
                                    <span className="text-sm bg-purple-100 text-purple-800 px-3 py-1 rounded-full">{flight.flight_id}</span>
                                    {isSurge && (
