import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Toaster, toast } from 'react-hot-toast';
import Navbar from './components/Navbar';
import FlightSearch from './components/FlightSearch';
import FlightList from './components/FlightList';
import BookingForm from './components/BookingForm';
import Wallet from './components/Wallet';
import BookingHistory from './components/BookingHistory';
import BookingSuccess from './components/BookingSuccess';
import './styles/purple-theme.css';

const API_BASE = 'http://localhost:5001/api';

const App = () => {
  const [user, setUser] = useState(null);
  const [flights, setFlights] = useState([]);
  const [selectedFlight, setSelectedFlight] = useState(null);
  const [bookings, setBookings] = useState([]);
 const navigate = useNavigate();
  /* =========================
     INIT USER (NO HARDCODING)
     ========================= */
  useEffect(() => {
    const initUser = async () => {
      try {
        const savedUser = localStorage.getItem('flightUser');
        if (savedUser) {
          setUser(JSON.parse(savedUser));
          return;
        }

        // Auto-create or fetch user from DB
        const res = await axios.post(`${API_BASE}/users/init`, {
          name: 'John Doe',
          email: 'john@example.com'
        });

        const newUser = {
          id: res.data._id,
          name: res.data.name,
          email: res.data.email,
          walletBalance: res.data.wallet_balance
        };

        localStorage.setItem('flightUser', JSON.stringify(newUser));
        setUser(newUser);
      } catch (err) {
        console.error('User init failed:', err);
        toast.error('Failed to initialize user');
      }
    };

    initUser();
  }, []);

  /* =========================
     FETCH BOOKINGS
     ========================= */
  useEffect(() => {
    if (!user?.id) return;

    const fetchBookings = async () => {
      try {
        const res = await axios.get(
          `${API_BASE}/bookings/user/${user.id}`
        );
        setBookings(res.data);
      } catch (err) {
        console.error('Fetch bookings failed:', err);
      }
    };

    fetchBookings();
  }, [user]);

  /* =========================
     SEARCH FLIGHTS
     ========================= */
  const handleSearchFlights = async (searchParams) => {
    try {
      const res = await axios.get(`${API_BASE}/flights/search`, {
        params: searchParams
      });
      setFlights(res.data);
      toast.success(`Found ${res.data.length} flights`);
    } catch {
      toast.error('Error searching flights');
    }
  };

  /* =========================
     BOOKING ATTEMPT (SURGE)
     ========================= */
  const handleBookingAttempt = async (flightId) => {
    try {
      const res = await axios.post(
        `${API_BASE}/flights/${flightId}/attempt`,
        { userId: user.id }
      );

      setFlights(prev =>
        prev.map(f =>
          f.flight_id === flightId
            ? { ...f, current_price: res.data.current_price }
            : f
        )
      );

      if (res.data.surge_active) {
        toast.error('‚ö†Ô∏è Surge pricing applied!', { duration: 4000 });
      }

      return res.data;
    } catch (err) {
      console.error('Booking attempt error:', err);
      return null;
    }
  };

  const handleBookFlight = async (flightId, passengerInfo) => {
  try {
    const res = await axios.post(`${API_BASE}/bookings`, {
      userId: user.id,
      flightId,
      passengerInfo
    });

    if (!res.data.success) return false;

    // Update wallet
    const updatedUser = {
      ...user,
      walletBalance: res.data.new_balance
    };

    setUser(updatedUser);
    localStorage.setItem('flightUser', JSON.stringify(updatedUser));

    // Update bookings
    setBookings(prev => [res.data.booking, ...prev]);

    // üöÄ Navigate AFTER returning true
    setTimeout(() => {
      navigate('/success', { state: { booking: res.data.booking } });
    }, 0);

    return true; // üî• THIS WAS MISSING / INCONSISTENT
  } catch (err) {
    console.error(err);
    toast.error(err.response?.data?.error || 'Booking failed');
    return false;
  }
};

  /* =========================
     ADD WALLET FUNDS
     ========================= */
  const handleAddFunds = async (amount) => {
    try {
      const res = await axios.post(
        `${API_BASE}/wallet/${user.id}/add`,
        { amount }
      );

      const updatedUser = {
        ...user,
        walletBalance: res.data.new_balance
      };

      setUser(updatedUser);
      localStorage.setItem('flightUser', JSON.stringify(updatedUser));

      toast.success(`‚Çπ${amount} added to wallet`);
      return true;
    } catch {
      toast.error('Failed to add funds');
      return false;
    }
  };

  /* =========================
     LOADING STATE
     ========================= */
  if (!user) {
    return (
      <div className="text-center mt-24 text-purple-600 text-lg">
        Initializing user...
      </div>
    );
  }

  /* =========================
     UI
     ========================= */
  return (
      <div className="min-h-screen bg-gradient-to-br from-purple-50 to-pink-50">
        <Toaster
          position="top-right"
          toastOptions={{
            style: {
              background: '#6B46C1',
              color: '#fff'
            }
          }}
        />

        <Navbar user={user} />

        <div className="container mx-auto px-4 py-8">
          <Routes>
            <Route
              path="/"
              element={
                <>
                  <div className="text-center mb-12">
                    <h1 className="text-4xl font-bold text-purple-900 mb-4">
                      ‚úàÔ∏è Find Your Perfect Flight
                    </h1>
                    <p className="text-purple-600 text-lg">
                      Dynamic pricing ‚Ä¢ Wallet payments ‚Ä¢ Instant tickets
                    </p>
                  </div>

                  <FlightSearch onSearch={handleSearchFlights} />

                  {flights.length > 0 && (
                    <FlightList
                      flights={flights}
                      onSelectFlight={setSelectedFlight}
                      onBookingAttempt={handleBookingAttempt}
                    />
                  )}
                </>
              }
            />

            <Route
              path="/book"
              element={
                selectedFlight ? (
                  <BookingForm
                    flight={selectedFlight}
                    user={user}
                    onBook={handleBookFlight}
                  />
                ) : (
                  <Navigate to="/" />
                )
              }
            />
            <Route path="/success" element={<BookingSuccess />} />
            <Route
              path="/wallet"
              element={
                <Wallet user={user} onAddFunds={handleAddFunds} />
              }
            />

            <Route
              path="/history"
              element={
                <BookingHistory
                  bookings={bookings}
                  onDownloadTicket={(pnr) =>
                    window.open(
                      `${API_BASE}/bookings/ticket/${pnr}`,
                      '_blank'
                    )
                  }
                />
              }
            />
          </Routes>
        </div>

        <footer className="bg-purple-900 text-white py-8 mt-12">
          <div className="container mx-auto text-center">
            {/* */}
            <p className="text-purple-300 mt-1">
              Built with ‚ù§Ô∏è by Tanya
            </p>
          </div>
        </footer>
      </div>
  );
};

export default App;
