import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FiHome, FiCreditCard, FiHistory, FiUser } from 'react-icons/fi';

const Navbar = ({ user }) => {
  const location = useLocation();

  const navItems = [
    { path: '/', label: 'Search Flights', icon: <FiHome /> },
    { path: '/wallet', label: 'Wallet', icon: <FiCreditCard /> },
    { path: '/history', label: 'History', icon: <FiHistory /> },
  ];

  return (
    <nav className="navbar sticky top-0 z-50">
      <div className="container mx-auto px-4">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="flex items-center gap-3">
              <div className="w-10 h-10 rounded-full purple-gradient-bg flex items-center justify-center">
                <span className="text-white font-bold text-xl">✈️</span>
              </div>
              <span className="text-xl font-bold text-purple-900">SkyBook</span>
            </Link>
          </div>

          <div className="flex items-center gap-6">
            {navItems.map((item) => (
              <Link
                key={item.path}
                to={item.path}
                className={`flex items-center gap-2 px-4 py-2 rounded-lg transition-colors ${
                  location.pathname === item.path
                    ? 'bg-purple-100 text-purple-700'
                    : 'text-purple-600 hover:bg-purple-50'
                }`}
              >
                {item.icon}
                <span className="font-medium">{item.label}</span>
              </Link>
            ))}

            <div className="flex items-center gap-3 pl-6 border-l border-purple-100">
              <div className="w-8 h-8 rounded-full bg-purple-100 flex items-center justify-center">
                <FiUser className="text-purple-600" />
              </div>
              <div>
                <div className="text-sm font-medium text-purple-900">{user.name}</div>
                <div className="text-xs text-purple-600">Balance: ₹{user.walletBalance.toLocaleString()}</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
