    //           value={password}
    //           onChange={e => setPassword(e.target.value)}
    //           className="input-field"
    //         />
    //       </div>
    //     </div>

    //     <button
    //       onClick={submit}
    //       disabled={loading}
    //       className="btn-primary w-full mt-4"
    //     >
    //       {loading ? 'Logging in...' : 'Login'}
    //     </button>

    //     <p className="text-sm text-center mt-4 text-purple-700">
    //       No account yet? <Link to="/register" className="font-semibold underline">Register</Link>
    //     </p>
    //   </div>
    // </div>
    <div className="bg-airline">
  <div className="card ">
    <h1 className="heading">Welcome Back ✈️</h1>

    <div className="space-y-5">
      <div className="input-group">
        <FiMail className="icon" />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          className="input-field"
        />
      </div>

      <div className="input-group">
        <FiLock className="icon" />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          className="input-field"
        />
      </div>
    </div>

    <button
      onClick={submit}
      disabled={loading}
      className="btn-primary mt-6 "
    >
      {loading ? 'Logging in...' : 'Login'}
    </button>

    <p className="text-sm text-center mt-4">
      No account yet? <Link to="/register" className="footer-link">Register</Link>
    </p>
  </div>
</div>

  );
}
