import './styles/purple-theme.css';

const API_BASE = 'http://localhost:5001/api';

const App = () => {
  const [user, setUser] = useState(null);
  const [flights, setFlights] = useState([]);
  const [selectedFlight, setSelectedFlight] = useState(null);
  const [bookings, setBookings] = useState([]);

  /* =========================
     INIT USER (NO HARDCODING)
     ========================= */
  useEffect(() => {
    const initUser = async () => {
      try {
        const savedUser = localStorage.getItem('flightUser');
        if (savedUser) {
          setUser(JSON.parse(savedUser));
          return;
        }

        // Auto-create or fetch user from DB
        const res = await axios.post(`${API_BASE}/users/init`, {
          name: 'John Doe',
          email: 'john@example.com'
        });

        const newUser = {
          id: res.data._id,
          name: res.data.name,
          email: res.data.email,
          walletBalance: res.data.wallet_balance
        };

        localStorage.setItem('flightUser', JSON.stringify(newUser));
        setUser(newUser);
      } catch (err) {
        console.error('User init failed:', err);
        toast.error('Failed to initialize user');
      }
    };

    initUser();
  }, []);

  /* =========================
     FETCH BOOKINGS
     ========================= */
  useEffect(() => {
    if (!user?.id) return;

    const fetchBookings = async () => {
      try {
        const res = await axios.get(
          `${API_BASE}/bookings/user/${user.id}`
        );
        setBookings(res.data);
      } catch (err) {
        console.error('Fetch bookings failed:', err);
      }
    };

    fetchBookings();
  }, [user]);

  /* =========================
     SEARCH FLIGHTS
     ========================= */
  const handleSearchFlights = async (searchParams) => {
    try {
      const res = await axios.get(`${API_BASE}/flights/search`, {
        params: searchParams
      });
      setFlights(res.data);
      toast.success(`Found ${res.data.length} flights`);
    } catch {
      toast.error('Error searching flights');
    }
  };

  /* =========================
     BOOKING ATTEMPT (SURGE)
     ========================= */
  const handleBookingAttempt = async (flightId) => {
    try {
      const res = await axios.post(
        `${API_BASE}/flights/${flightId}/attempt`,
        { userId: user.id }
      );

      setFlights(prev =>
        prev.map(f =>
          f.flight_id === flightId
            ? { ...f, current_price: res.data.current_price }
            : f
        )
      );

      if (res.data.surge_active) {
        toast.error('‚ö†Ô∏è Surge pricing applied!', { duration: 4000 });
      }

      return res.data;
    } catch (err) {
      console.error('Booking attempt error:', err);
      return null;
    }
  };

  /* =========================
