import React from 'react';
import { CheckCircle } from 'lucide-react';
import { Link, useLocation, Navigate } from 'react-router-dom';

export default function BookingSuccess() {
  const { state } = useLocation();

  if (!state?.booking) {
    return <Navigate to="/" />;
  }

  const { booking } = state;

  return (
    <div className="max-w-3xl mx-auto mt-16 bg-white rounded-2xl shadow-xl p-10 text-center">
      <CheckCircle className="mx-auto text-green-500" size={64} />
      <h1 className="text-3xl font-bold mt-4 text-purple-900">
        Booking Confirmed
      </h1>

      <p className="mt-2 text-gray-600">
        PNR: <span className="font-semibold">{booking.pnr}</span>
      </p>

      <div className="mt-6 grid grid-cols-2 gap-4 text-left">
        <div>
          <b>Passenger</b>
          <p>{booking.passenger_name}</p>
        </div>
        <div>
          <b>Email</b>
          <p>{booking.passenger_email}</p>
        </div>
        <div>
          <b>Route</b>
          <p>
            {booking.flight_details.departure_city} →{' '}
            {booking.flight_details.arrival_city}
          </p>
        </div>
        <div>
          <b>Amount Paid</b>
          <p>₹{booking.final_price}</p>
        </div>
      </div>

      <div className="mt-8 flex justify-center gap-4">
        <a
          href={`http://localhost:5001/api/bookings/ticket/${booking.pnr}`}
          className="btn-primary"
        >
          Download Ticket
        </a>

        <Link to="/history" className="btn-secondary">
          View Bookings
        </Link>
      </div>
    </div>
  );
}
