import React, { useState } from 'react';
import { FiDollarSign, FiClock, FiPlusCircle } from 'react-icons/fi';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { useEffect } from 'react';
import axios from 'axios';
const API_BASE = process.env.REACT_APP_API_BASE;
const Wallet = ({ user, onAddFunds }) => {
  const [amount, setAmount] = useState(1000);
  const [isAdding, setIsAdding] = useState(false);

  const walletHistory = [
    { date: 'Jan', balance: 45000 },
    { date: 'Feb', balance: 42000 },
    { date: 'Mar', balance: 48000 },
    { date: 'Apr', balance: 46000 },
    { date: 'May', balance: 50000 },
    // { date: 'Jun', balance: user.walletBalance },
     { date: 'Jun', balance: user.walletBalance ?? 0 },
  ];

//   const recentTransactions = [
//     { type: 'debit', amount: 2500, description: 'Flight Booking - FL012', date: '2024-06-15' },
//     { type: 'credit', amount: 10000, description: 'Wallet Top-up', date: '2024-06-10' },
//     { type: 'debit', amount: 2800, description: 'Flight Booking - FL008', date: '2024-06-05' },
//     { type: 'debit', amount: 2200, description: 'Flight Booking - FL005', date: '2024-05-28' },
//     { type: 'credit', amount: 5000, description: 'Wallet Top-up', date: '2024-05-25' },
//   ];
  const [recentTransactions, setRecentTransactions] = useState([]);

useEffect(() => {
  const fetchTransactions = async () => {
    const res = await axios.get(`${API_BASE}/wallet/${user.id}/history`);
    setRecentTransactions(res.data);
  };
  fetchTransactions();
}, [user]);

  const handleAddFunds = async () => {
    if (amount <= 0) {
      alert('Please enter a valid amount');
      return;
    }

    setIsAdding(true);
    const success = await onAddFunds(amount);
    setIsAdding(false);

    if (success) {
      setAmount(1000);
    }
  };

  return (
    <div className="max-w-6xl mx-auto fade-in">
      <h1 className="text-3xl font-bold text-purple-900 mb-8">
        <FiDollarSign className="inline mr-3" />
        My Wallet
      </h1>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        <div className="lg:col-span-2">
          <div className="card mb-8">
            <h2 className="text-xl font-bold text-purple-900 mb-6">
              Wallet Balance History
            </h2>

            <div className="h-64">
              <ResponsiveContainer width="100%" height="100%">
                <LineChart data={walletHistory}>
                  <CartesianGrid strokeDasharray="3 3" stroke="#E9D8FD" />
                  <XAxis dataKey="date" stroke="#6B46C1" />
                  <YAxis stroke="#6B46C1" />
                  <Tooltip 
                    contentStyle={{ 
                      background: '#6B46C1', 
                      color: 'white',
                      border: 'none',
                      borderRadius: '8px'
                    }}
                  />
                  <Line 
                    type="monotone" 
                    dataKey="balance" 
                    stroke="#9F7AEA" 
                    strokeWidth={3}
                    dot={{ stroke: '#6B46C1', strokeWidth: 2, r: 4 }}
                    activeDot={{ r: 6, fill: '#6B46C1' }}
                  />
                </LineChart>
              </ResponsiveContainer>
            </div>
          </div>

          <div className="card">
            <h2 className="text-xl font-bold text-purple-900 mb-6">
              <FiClock className="inline mr-3" />
              Recent Transactions
            </h2>

            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b border-purple-100">
                    <th className="text-left py-3 px-4 text-purple-600">Type</th>
                    <th className="text-left py-3 px-4 text-purple-600">Amount</th>
                    <th className="text-left py-3 px-4 text-purple-600">Description</th>
                    <th className="text-left py-3 px-4 text-purple-600">Date</th>
                  </tr>
                </thead>
                <tbody>
                  {recentTransactions.map((tx, index) => (
                    <tr key={index} className="border-b border-purple-50 hover:bg-purple-50">
                      <td className="py-3 px-4">
                        <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                          tx.type === 'credit' 
                            ? 'bg-green-100 text-green-800'
                            : 'bg-red-100 text-red-800'
                        }`}>
                          {tx.type === 'credit' ? 'Credit' : 'Debit'}
                        </span>
                      </td>
                      <td className={`py-3 px-4 font-semibold ${
                        tx.type === 'credit' ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {tx.type === 'credit' ? '+' : '-'}₹{tx.amount.toLocaleString()}
                      </td>
                      <td className="py-3 px-4 text-purple-600">{tx.description}</td>
                      <td className="py-3 px-4 text-purple-600">{tx.date}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>

        <div>
          <div className="card-purple card mb-8">
            <div className="text-center">
              <div className="text-5xl font-bold mb-4">₹{user.walletBalance.toLocaleString()}</div>
              <p className="text-purple-200">Current Wallet Balance</p>
            </div>
          </div>

          <div className="card">
            <h2 className="text-xl font-bold text-purple-900 mb-6">
              <FiPlusCircle className="inline mr-3" />
              Add Funds
            </h2>

            <div className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-purple-700 mb-2">
                  Amount (₹)
                </label>
                <input
                  type="number"
                  value={amount}
                  onChange={(e) => setAmount(parseInt(e.target.value) || 0)}
                  min="100"
                  step="100"
                  className="input-field"
                  placeholder="Enter amount"
                />
              </div>

              <div className="grid grid-cols-2 gap-3">
                {[500, 1000, 2000, 5000].map((amt) => (
                  <button
                    key={amt}
                    onClick={() => setAmount(amt)}
                    className={`py-3 text-center rounded-lg border-2 transition-all ${
                      amount === amt
                        ? 'border-purple-600 bg-purple-50 text-purple-700'
                        : 'border-purple-200 text-purple-600 hover:border-purple-400'
                    }`}
                  >
                    ₹{amt}
                  </button>
                ))}
              </div>

              <button
                onClick={handleAddFunds}
                disabled={isAdding}
                className="btn-primary w-full"
              >
                {isAdding ? (
                  <>
                    <span className="animate-spin inline-block mr-2">⟳</span>
                    Processing...
                  </>
                ) : (
                  <>
                    <FiPlusCircle className="inline mr-2" />
                    Add ₹{amount} to Wallet
                  </>
                )}
              </button>
            </div>
          </div>

          <div className="card mt-8">
            <h3 className="font-bold text-purple-900 mb-4">Quick Tips</h3>
            <ul className="space-y-3 text-sm text-purple-600">
              <li className="flex items-start gap-2">
                <span className="text-purple-500">•</span>
                Minimum top-up amount is ₹100
              </li>
              <li className="flex items-start gap-2">
                <span className="text-purple-500">•</span>
                Funds are added instantly
              </li>
              <li className="flex items-start gap-2">
                <span className="text-purple-500">•</span>
                Wallet balance never expires
              </li>
              <li className="flex items-start gap-2">
                <span className="text-purple-500">•</span>
                Get 5% bonus on ₹5000+ top-ups
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Wallet;
