import React from 'react';
import { FiCalendar, FiDownload, FiMapPin } from 'react-icons/fi';
import { format } from 'date-fns';

const BookingHistory = ({ bookings, onDownloadTicket }) => {
    return (
        <div className="max-w-6xl mx-auto fade-in px-4 md:px-0">
            <h1 className="text-3xl font-bold text-purple-900 mb-8 flex items-center gap-2">
                <FiCalendar className="text-purple-700" />
                Booking History
            </h1>

            {bookings.length === 0 ? (
                <div className="card text-center py-12 bg-purple-50 rounded-xl shadow-md">
                    <div className="text-6xl mb-6 text-purple-200">✈️</div>
                    <h3 className="text-xl font-bold text-purple-700 mb-3">No Bookings Yet</h3>
                    <p className="text-purple-600">Start your journey by booking your first flight!</p>
                </div>
            ) : (
                <div className="space-y-6">
                    {bookings.map((booking) => (
                        {/* <div key={booking._id} className="card bg-white rounded-xl shadow-md p-6">
                            <div className="flex flex-col md:flex-row md:items-center justify-between gap-6">

                                <div className="flex-1 space-y-4">

                                    <div className="flex flex-wrap items-center gap-4">
                                        <div className="bg-purple-100 text-purple-800 px-4 py-2 rounded-lg text-center">
                                            <div className="text-sm font-medium">PNR</div>
                                            <div className="font-bold text-lg">{booking.pnr}</div>
                                        </div>

                                        <div className="text-sm">
                                            <div className="text-purple-600">Booking Date</div>
                                            <div className="font-semibold">
                                                {format(new Date(booking.booking_date), 'MMM dd, yyyy')}
                                            </div>
                                        </div>

                                        <div className="text-sm">
                                            <div className="text-purple-600">Status</div>
                                            <div className={`px-3 py-1 rounded-full text-xs font-medium ${booking.status === 'confirmed'
                                                    ? 'bg-green-100 text-green-800'
                                                    : 'bg-yellow-100 text-yellow-800'
                                                }`}>
                                                {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                                            </div>
                                        </div>
                                    </div>


                                    <div className="space-y-2">
                                        <div className="flex items-center gap-2 text-purple-900 font-semibold">
                                            <FiMapPin className="text-purple-600" />
                                            {booking.flight_details.departure_city} → {booking.flight_details.arrival_city}
                                        </div>

                                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm text-purple-600">
                                            <div>
                                                <div className="font-medium text-purple-900">{booking.flight_details.airline}</div>
                                                <div>Airline</div>
                                            </div>
                                            <div>
                                                <div className="font-medium text-purple-900">{booking.passenger_name}</div>
                                                <div>Passenger</div>
                                            </div>
                                            <div>
                                                <div className="font-medium text-purple-900">
                                                    {format(new Date(booking.flight_details.departure_time), 'MMM dd, yyyy')}
                                                </div>
                                                <div>Travel Date</div>
                                            </div>
                                        </div>
                                    </div>
                                </div>


                                <div className="flex flex-col items-end gap-4">
