import React, { useState, useEffect } from 'react';
import { FiClock, FiMapPin, FiTrendingUp, FiCalendar } from 'react-icons/fi';
import { useNavigate } from 'react-router-dom';
import { format } from 'date-fns';

const FlightList = ({ flights, onSelectFlight, onBookingAttempt }) => {
  const navigate = useNavigate();
  const [attemptTimers, setAttemptTimers] = useState({});
  const handleViewDetails = async (flight) => {
    // Record booking attempt
    const result = await onBookingAttempt(flight.flight_id);

    if (result) {
      // Set timer for surge reset
      if (result.surge_reset_time) {
        setAttemptTimers(prev => ({
          ...prev,
          [flight.flight_id]: result.surge_reset_time
        }));
      }

      onSelectFlight(flight);
      navigate('/book');
    }
  };

  const getTimeRemaining = (resetTime) => {
    if (!resetTime) return null;

    const now = new Date();
    const reset = new Date(resetTime);
    const diff = reset - now;

    if (diff <= 0) return null;

    const minutes = Math.floor(diff / 60000);
    const seconds = Math.floor((diff % 60000) / 1000);

    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  // Update timers every second
  useEffect(() => {
    const interval = setInterval(() => {
      setAttemptTimers(prev => {
        const updated = { ...prev };
        Object.keys(updated).forEach(flightId => {
          const timeRemaining = getTimeRemaining(updated[flightId]);
          if (!timeRemaining) {
            delete updated[flightId];
          }
        });
        return updated;
      });
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold text-purple-900 mb-6">Available Flights</h2>

      {flights.map(flight => {
        const isSurge = flight.surge_multiplier > 1;
        const timeRemaining = getTimeRemaining(attemptTimers[flight.flight_id]);

        return (
          <div 
            key={flight.flight_id} 
            className={`flight-card card ${isSurge ? 'surge' : ''}`}
          >
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
              <div className="flex-1">
                <div className="flex items-center gap-4 mb-3">
                  <span className="text-xl font-bold text-purple-900">
                    {flight.airline}
                  </span>
                  <span className="text-sm bg-purple-100 text-purple-800 px-3 py-1 rounded-full">
                    {flight.flight_id}
                  </span>
                  {isSurge && (
                    <span className="surge-badge">
                      <FiTrendingUp className="inline mr-1" />
                      Surge Pricing Active
                    </span>
                  )}
                </div>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                  <div className="flex items-center gap-2">
                    <FiMapPin className="text-purple-600" />
                    <div>
                      <p className="text-sm text-purple-600">From</p>
                      <p className="font-semibold">{flight.departure_city}</p>
                    </div>
                  </div>

                  <div className="flex items-center justify-center">
                    <div className="w-full border-t-2 border-dashed border-purple-300 relative">
                      <div className="absolute left-1/2 transform -translate-x-1/2 -top-2 bg-white px-2">
                        <FiClock className="text-purple-600" />
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center gap-2 justify-end md:justify-start">
                    <FiMapPin className="text-purple-600" />
                    <div>
                      <p className="text-sm text-purple-600">To</p>
                      <p className="font-semibold">{flight.arrival_city}</p>
                    </div>
                  </div>
                </div>

                <div className="flex items-center gap-6 text-sm text-purple-600">
                  <div className="flex items-center gap-2">
                    <FiCalendar />
                    <span>{format(new Date(flight.departure_time), 'MMM dd, hh:mm a')}</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <FiClock />
                    <span>
                      {Math.round((new Date(flight.arrival_time) - new Date(flight.departure_time)) / (1000 * 60 * 60))}h
                    </span>
                  </div>
                  <div className="flex items-center gap-2">
                    <span className="font-semibold">Seats:</span>
                    <span>{flight.available_seats} available</span>
                  </div>
                </div>
              </div>

              <div className="flex flex-col items-end gap-3">
                <div className="text-right">
                  {isSurge && (
                    <p className="text-sm text-gray-500 line-through">
                      ₹{flight.base_price}
                    </p>
                  )}
                  <p className={`text-2xl font-bold ${isSurge ? 'text-red-600' : 'text-purple-900'}`}>
                    ₹{flight.current_price}
                  </p>
                  {isSurge && (
                    <p className="text-xs text-red-600">
                      +{((flight.surge_multiplier - 1) * 100).toFixed(0)}% surge
                    </p>
                  )}
                </div>

                {timeRemaining && (
                  <div className="countdown-timer">
                    <FiClock />
                    <span>Resets in: {timeRemaining}</span>
                  </div>
                )}

                <button
                  onClick={() => handleViewDetails(flight)}
                  className="btn-primary"
                >
                  Book Now
                </button>

              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

export default FlightList;
