import React, { useState } from 'react';
import { FiUser, FiMail, FiPhone, FiCreditCard, FiCheckCircle } from 'react-icons/fi';
import { format } from 'date-fns';
import { toast } from 'react-hot-toast';

const BookingForm = ({ flight, user, onBook }) => {
  if (!flight || !user) {
    return (
      <div className="text-center mt-24 text-purple-600 text-lg animate-pulse">
        Loading booking details...
      </div>
    );
  }

  const [passengerInfo, setPassengerInfo] = useState({
    name: user.name || '',
    email: user.email || '',
    phone: ''
  });

  const [isBooking, setIsBooking] = useState(false);
  const [errors, setErrors] = useState({});

  const price = Number(flight.current_price);
  const walletBalance = Number(user.walletBalance);
  const hasEnoughBalance = walletBalance >= price;

  const handleChange = (e) => {
    setPassengerInfo({
      ...passengerInfo,
      [e.target.name]: e.target.value
    });
    setErrors({ ...errors, [e.target.name]: '' });
  };

  const validate = () => {
    const newErrors = {};
    if (!passengerInfo.name.trim()) newErrors.name = 'Name is required';
    if (!/\S+@\S+\.\S+/.test(passengerInfo.email)) newErrors.email = 'Invalid email';
    if (!/^\d{10}$/.test(passengerInfo.phone)) newErrors.phone = 'Phone must be 10 digits';
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!hasEnoughBalance) {
      toast.error('Insufficient wallet balance');
      return;
    }
    if (!validate()) return;

    try {
      setIsBooking(true);
      await onBook(flight.flight_id, passengerInfo);
      toast.success('Booking confirmed!');
    } catch (err) {
      console.error(err);
      toast.error('Something went wrong during booking');
    } finally {
      setIsBooking(false);
    }
  };

  return (
    <div className="max-w-5xl mx-auto px-4 fade-in">
      <h1 className="text-3xl font-bold text-purple-900 mb-8 text-center">
        Complete Your Booking
      </h1>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* LEFT: Passenger Details */}
        <div className="lg:col-span-2">
          <div className="card shadow-lg rounded-xl border border-purple-200 p-6 bg-white">
            <h2 className="text-xl font-bold text-purple-900 mb-6">Passenger Details</h2>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-purple-700 mb-2">
                  <FiUser className="inline mr-2 text-purple-600" />
                  Full Name
                </label>
                <input
                  type="text"
                  name="name"
                  value={passengerInfo.name}
                  onChange={handleChange}
                  className={`input-field border rounded-lg w-full px-4 py-2 focus:ring-2 focus:ring-purple-400 focus:outline-none ${
                    errors.name ? 'border-red-500' : 'border-purple-300'
                  }`}
                  required
                />
                {errors.name && <p className="text-red-500 text-sm mt-1">{errors.name}</p>}
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div>
                  <label className="block text-sm font-medium text-purple-700 mb-2">
                    <FiMail className="inline mr-2 text-purple-600" />
                    Email
                  </label>
                  <input
                    type="email"
                    name="email"
                    value={passengerInfo.email}
                    onChange={handleChange}
                    className={`input-field border rounded-lg w-full px-4 py-2 focus:ring-2 focus:ring-purple-400 focus:outline-none ${
                      errors.email ? 'border-red-500' : 'border-purple-300'
                    }`}
                    required
                  />
                  {errors.email && <p className="text-red-500 text-sm mt-1">{errors.email}</p>}
                </div>

                <div>
                  <label className="block text-sm font-medium text-purple-700 mb-2">
                    <FiPhone className="inline mr-2 text-purple-600" />
                    Phone
                  </label>
                  <input
                    type="tel"
                    name="phone"
                    value={passengerInfo.phone}
                    onChange={handleChange}
                    className={`input-field border rounded-lg w-full px-4 py-2 focus:ring-2 focus:ring-purple-400 focus:outline-none ${
                      errors.phone ? 'border-red-500' : 'border-purple-300'
                    }`}
                    required
                  />
                  {errors.phone && <p className="text-red-500 text-sm mt-1">{errors.phone}</p>}
                </div>
              </div>

              <button
                type="submit"
                disabled={isBooking || !hasEnoughBalance}
                className={`btn-primary w-full flex items-center justify-center bg-purple-600 hover:bg-purple-700 text-white font-semibold py-3 px-6 rounded-lg shadow-md transition duration-300 ${
                  !hasEnoughBalance ? 'opacity-50 cursor-not-allowed' : ''
                }`}
              >
                {isBooking ? (
                  <>Processing...</>
                ) : (
                  <>
                    <FiCreditCard className="inline mr-2" /> Confirm Booking – ₹{price}
                  </>
                )}
              </button>

              {!hasEnoughBalance && (
                <p className="text-sm text-red-600 text-center mt-2">
                  Insufficient wallet balance. Please add funds.
                </p>
              )}
            </form>
          </div>
        </div>

        {/* RIGHT: Flight Summary */}
        {/* <div className="sticky top-8">
          <div className="card shadow-lg rounded-xl border border-purple-200 p-6 bg-white">
            <h2 className="text-xl font-bold text-purple-900 mb-6">Flight Summary</h2>

            <div className="space-y-3 text-sm">
              <div className="flex justify-between">
                <span>Flight</span>
                <span>{flight.airline} {flight.flight_id}</span>
              </div>
              <div className="flex justify-between">
                <span>Route</span>
                <span>{flight.departure_city} → {flight.arrival_city}</span>
              </div>
              <div className="flex justify-between">
                <span>Departure</span>
                <span>{format(new Date(flight.departure_time), 'MMM dd, hh:mm a')}</span>
              </div>
              <div className="flex justify-between font-bold pt-4 border-t">
                <span>Total</span>
                <span>₹{price}</span>
              </div>
            </div>

            <div className="wallet-balance mt-6 p-4 bg-purple-50 rounded-lg text-center">
              <FiCheckCircle className="inline mr-2 text-green-500" />
              <span className="text-purple-700 font-medium">Wallet Balance:</span>
              <div className="amount text-2xl font-bold text-purple-900">₹{walletBalance}</div>
            </div>
          </div>
        </div> */}
        <div className="sticky top-8">
  <div className="card shadow-xl rounded-2xl border border-purple-200 p-6 bg-gradient-to-b from-purple-50 to-white">
    <h2 className="text-2xl font-bold text-purple-900 mb-6 flex items-center justify-between">
      Flight Summary
      <span className="bg-purple-100 text-purple-800 px-3 py-1 rounded-full text-sm font-semibold">
        {flight.flight_id}
      </span>
    </h2>

    <div className="space-y-4">
      {/* Airline */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <FiCheckCircle className="text-purple-600" />
          <span className="text-sm font-medium text-purple-700">Airline</span>
        </div>
        <span className="font-semibold text-purple-900">{flight.airline}</span>
      </div>

      <div className="border-t border-purple-200" />

      {/* Route */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <FiMapPin className="text-purple-600" />
          <span className="text-sm font-medium text-purple-700">Route</span>
        </div>
        <span className="font-semibold text-purple-900">
          {flight.departure_city} → {flight.arrival_city}
        </span>
      </div>

      <div className="border-t border-purple-200" />

      {/* Departure */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <FiCalendar className="text-purple-600" />
          <span className="text-sm font-medium text-purple-700">Departure</span>
        </div>
        <span className="font-semibold text-purple-900">
          {format(new Date(flight.departure_time), 'MMM dd, hh:mm a')}
        </span>
      </div>

      <div className="border-t border-purple-200" />

      {/* Seats */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <FiUser className="text-purple-600" />
          <span className="text-sm font-medium text-purple-700">Seats Available</span>
        </div>
        <span className="font-semibold text-purple-900">{flight.available_seats}</span>
      </div>

      <div className="border-t border-purple-200" />

      {/* Price */}
      <div className="flex items-center justify-between pt-4">
        <span className="text-lg font-bold text-purple-900">Total Price</span>
        <span className="text-2xl font-extrabold text-purple-800">
          ₹{flight.current_price}
        </span>
      </div>
    </div>

    {/* Wallet */}
    <div className="wallet-balance mt-6 p-4 bg-purple-100 rounded-xl text-center">
      <FiCreditCard className="inline mr-2 text-purple-700" />
      <span className="text-purple-700 font-medium">Wallet Balance:</span>
      <div className="text-2xl font-bold text-purple-900 mt-1">₹{user.walletBalance}</div>
    </div>
  </div>
</div>

      </div>
    </div>
  );
};

export default BookingForm;
