import React from 'react';
import { FiCalendar, FiDownload, FiMapPin } from 'react-icons/fi';
import { format } from 'date-fns';

const BookingHistory = ({ bookings }) => {
  return (
    <div className="max-w-6xl mx-auto fade-in px-4 md:px-0">

      {/* ===== PAGE TITLE ===== */}
      <h1 className="text-3xl font-bold text-purple-900 mb-8 flex items-center gap-3">
        <FiCalendar className="text-purple-700" />
        Booking History
      </h1>

      {/* ===== EMPTY STATE ===== */}
      {bookings.length === 0 && (
        <div className="card text-center py-12">
          <div className="text-6xl mb-6 text-purple-200">✈️</div>
          <h3 className="text-xl font-bold text-purple-700 mb-2">
            No Bookings Yet
          </h3>
          <p className="text-purple-600">
            Start your journey by booking your first flight!
          </p>
        </div>
      )}

      {/* ===== BOOKINGS LIST ===== */}
      {bookings.length > 0 && (
        <div className="space-y-6">
          {bookings.map((booking) => (
            <div key={booking._id} className="card p-6">

              <div className="flex flex-col md:flex-row md:items-center justify-between gap-6">

                {/* ===== LEFT : BOOKING DETAILS ===== */}
                <div className="flex-1 space-y-4">

                  {/* Header Row */}
                  <div className="flex flex-wrap items-center gap-4">
                    <div className="bg-purple-100 text-purple-800 px-4 py-2 rounded-lg text-center">
                      <div className="text-xs">PNR</div>
                      <div className="font-bold text-lg">{booking.pnr}</div>
                    </div>

                    <InfoBlock
                      label="Booking Date"
                      value={format(new Date(booking.booking_date), 'MMM dd, yyyy')}
                    />

                    <StatusBadge status={booking.status} />
                  </div>

                  {/* Route */}
                  <div className="flex items-center gap-2 font-semibold text-purple-900">
                    <FiMapPin className="text-purple-600" />
                    {booking.flight_details.departure_city} →{' '}
                    {booking.flight_details.arrival_city}
                  </div>

                  {/* Meta Info */}
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                    <MetaBlock
                      label="Airline"
                      value={booking.flight_details.airline}
                    />
                    <MetaBlock
                      label="Passenger"
                      value={booking.passenger_name}
                    />
                    <MetaBlock
                      label="Travel Date"
                      value={format(
                        new Date(booking.flight_details.departure_time),
                        'MMM dd, yyyy'
                      )}
                    />
                  </div>
                </div>

                {/* ===== RIGHT : PRICE & ACTION ===== */}
                <div className="flex flex-col items-end gap-4">

                  {/* Price */}
                  <div className="text-right">
                    <div className="text-2xl font-bold text-purple-900">
                      ₹{booking.final_price}
                    </div>

                    {booking.base_price !== booking.final_price && (
                      <div className="text-sm text-gray-500 line-through">
                        ₹{booking.base_price}
                      </div>
                    )}

                    <div className="text-sm text-purple-600">Final Price</div>
                  </div>

                  {/* Download */}
                  <a
                    href={`http://localhost:5001/api/bookings/ticket/${booking.pnr}`}
                    className="btn-secondary flex items-center gap-2"
                  >
                    <FiDownload />
                    Download Ticket
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* ===== SUMMARY ===== */}
      {bookings.length > 0 && (
        <div className="card-purple card mt-8 p-6 text-center">
          <div className="flex flex-col sm:flex-row justify-center gap-8">
            <SummaryItem label="Total Bookings" value={bookings.length} />
            <SummaryItem
              label="Total Spent"
              value={`₹${bookings.reduce(
                (sum, b) => sum + b.final_price,
                0
              ).toLocaleString()}`}
            />
            <SummaryItem
              label="Airlines"
              value={
                new Set(bookings.map(b => b.flight_details.airline)).size
              }
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default BookingHistory;
